<script>
	// 	SHOWING DYNAMIC FORM FOR THE PLANS USING ONCLICK FUNCTIONS ON THE PLANS SUBMIT BUTTON

	function plan(title) {

		$(document).ready(function () {

			var the_title_1 = ((title.toLowerCase()).charAt(0)).toUpperCase();
			var the_title_2 = (title.toLowerCase()).slice(1);


			$("#exampleModalLabel").html(the_title_1 + the_title_2 + ' Plan');

			$('input[name="plan"]').val(title);

		});

	}
</script>



