/* The popup form - hidden by default */

.form-popup {

  display: none;

  position: fixed;

  bottom: 0;

  right: 15px;

  border: 3px solid #f1f1f1;

  z-index: 9;

}



/* Add styles to the form container */

.form-container {

  max-width: 300px;

  padding: 10px;

  background-color: white;

}



/* Full-width input fields */

.form-container input[type=text], .form-container input[type=email], .form-container input[type=password] {

  width: 100%;

  padding: 10px;

  margin: 5px 0 5px 0;

  border: none;

  background: #f1f1f1;

}



/* When the inputs get focus, do something */

.form-container input[type=text]:focus, .form-container input[type=email]:focus, .form-container input[type=password]:focus {

  background-color: #ddd;

  outline: none;

}



/* Set a style for the submit/login button */

.form-container .btn {

  background-color: #9E0C10;

  color: white;

  padding: 16px 20px;

  border: none;

  cursor: pointer;

  width: 100%;

  margin-bottom:10px;

  opacity: 0.8;
      font-size: 13px;

}



/* Add a red background color to the cancel button */

.form-container .cancel {

  background-color: #000;

}



