	<script type="text/javascript" language="JavaScript">
function isEmpty(s) { return ((s == null) || (s.length == 0)); }
var whitespace = " \t\n\r";
function isWhitespace (s) {
  var i;
  if (isEmpty(s)) return true;
  for (i = 0; i < s.length; i++) {
    var c = s.charAt(i);
    if (whitespace.indexOf(c) == -1) return false;
  }
  return true;
}
var numb = "0123456789";
var lwr  = "abcdefghijklmnopqrstuvwxyz";
var upr  = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
var phspl  = "-() ";
var spc  = " ";
var eml  = "@_.";
var dot  = ".";
function isValid(parm,val) {
  if (parm == "") return true;
  for (i=0; i<parm.length; i++) {
    if (val.indexOf(parm.charAt(i),0) == -1) return false;
  }
  return true;
}
function doesExist (s) { return ( ! isEmpty(s) && ! isWhitespace (s) ); }
function validateForm() {
  var form = document.LoginForm;
  if ( form.user ) {
    if ( ! doesExist ( form.user.value ) ) {
      document.getElementById('userErr').innerHTML = "Please Enter the UserName";
      form.user.focus();
      return ( false );
    }
    else{
      document.getElementById('userErr').innerHTML = "";
    }
  }
  if ( form.pass ) {
    if ( ! doesExist ( form.pass.value ) ) {
      document.getElementById('userErr').innerHTML = "Please Enter the Password";
      form.pass.focus();
      return ( false );
    }
    else{
      document.getElementById('userErr').innerHTML = "";
    }
  }
  return ( true );
}
function validateAndSubmit() {
  var form = document.LoginForm;
  var ok = validateForm ();
  if ( ok ) form.submit ();
  return ( ok );
}
</script>
