           $(document).on('submit', '.quick-service', function (e) {
             e.preventDefault();
             $('.loader').fadeIn();
             var data =  $('.quick-service').serialize();
             // alert(data);
             $.ajax({
                 url:'quick-service.php',
                 type:'POST',
                 data:data,
                 dataType : 'json',
                 success:function(data){
                   $('.loader').fadeOut('slow');
                  if (data.status == false) {
                    Swal.fire({
                      position: 'top-center',
                      icon: 'error',
                      title: data.message,
                      showConfirmButton: false,
                      timer: 3000
                    });
                  }else {
                      swal.fire({
                        position: 'top-center',
                        icon: 'success',
                        title: data.message,
                        showConfirmButton: false,
                        timer: 3000
                      })
                    // alert(data.message);
                    $('.quick-service').trigger("reset");
                  }
                 }
               });
           })
     })
</script>
