      $('#Save').click(function(){
          
        var name = $('#name').val();
        var mob = $('#mob').val();
        var email = $('#email').val();
        var mobHidden = $('#mobHidden').val();
        var emailHidden = $('#emailHidden').val();
         if(name==""){
             alert('Name cannot be blank!');
             return false;
         }else if(mob=="" && email==""){
             
             alert ('Mobile or Email cannot be blank!');
             return false;
         }else if(email!=""){
             
             var verifyMail = /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/.test(email);
            if (verifyMail==false){
               alert("You have entered an invalid email address!")
            return false;
                
            }
            
            if(emailHidden!=""){
             alert('Email already Registered!');
             return false;
         }
            
         }else if(mob!=""){
             
             if(mobHidden!=""){
             alert('Mobile Number already Registered!');
             return false;
            }
             
         }
         
         var r = confirm ("Do you want to Save!");
         if (r) {
             return true; }
             else {
                 return false; 
             }
      });
      
    function mobEmailChk(mob,email){
        
        if(mob){
            
           $.ajax({
            type: "POST",
            url: "ajaxvalidation.php",
            data:{Mobile:mob},
            success: function(data){
             if(data=="mobFound"){
                 //$('#mob').val('');
                    $('#mobHidden').val(mob);
                 //alert('Mobile numbr Already registered!');
                 //return false;
             }
             
            }
        });
        
            
        }
        
        if(email){
           
           $.ajax({
            type: "POST",
            url: "ajaxvalidation.php",
            data:{Email:email},
            success: function(data){
             if(data=="emailFound"){
                 //$('#email').val('');
                 $('#emailHidden').val(email);
             }
             
            }
        });
        
            
        }
    }
    
    
    function isNumber(evt) {
    var charCode = (evt.which) ? evt.which : event.keyCode
      if (charCode != 46 && charCode > 31 
    	&& (charCode < 48 || charCode > 57))
    	 return false;
    
      return true;
    }
